#!/usr/bin/env ruby
# encoding: utf-8

number = '

'.strip

current = 0
max_v = 0

for i in 0..(number.length - 12)
  if current == 0
    current = 1
    for j in 0..12
      ch = number[i+j]
      if ch.to_i == 0
        current = 0
        i += j
        next
      else
        current *= ch.to_i
      end
    end
  else
    if number[i+12].to_i == 0
      current = 0
    else
      current *= number[i+12].to_i
      current /= number[i-1].to_i
      max_v = [current, max_v].max
    end
  end
end

puts max_v
